mapper.select:
SELECT 
    id,
    user_id,
    check_in_date,
    check_in_time,
    continuous_days,
    created_at
FROM check_in_record
WHERE user_id = #{userId}

    AND check_in_date >= #{startDate}


    AND check_in_date <= #{endDate}

ORDER BY check_in_date DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM check_in_record
WHERE user_id = #{userId}

    AND check_in_date >= #{startDate}


    AND check_in_date <= #{endDate}
